window.onload=function(){$(function(){var t=document.getElementById("tabContainer"),a=t.querySelector(".tabs ul li"),e=a.id.split("_")[1];a.parentNode.setAttribute("data-current",e),a.setAttribute("class","activeTab");for(var r=t.querySelectorAll(".tabpage"),i=1;r.length>i;i++)r[i].style.display="none";$(".tabs ul li").on("click",function(){var t=$(this).parent("ul").attr("data-current");$("#tabHeader_"+t).removeClass("activeTab"),$("#tabpage_"+t).css("display","none");var a=this.id.split("_")[1];$(this).attr("class","activeTab"),$("#tabpage_"+a).css("display","block"),$(this).parent("ul").attr("data-current",a)})})};